mapper.select:
/*不用声明list，可以检测到*/
       select  from discuss_post
       where status != 2   /*帖子状态不能是拉黑*/
       /*if判断 userId 不能为0*/
           and user_id = #{userId}

       order by type desc, create_time desc    /*类型和发帖时间的联合倒序*/
       limit #{offset}, #{limit}               /*分页*/

select count(id) from discuss_post
where status != 2
/*if判断 userId 不能为0*/
    and user_id = #{userId}
